    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
  try{
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
          
              PreparedStatement pst = cn.prepareStatement("insert into anuales values(?,?,?,?,?,?)");
            
         
            pst.setString(1, (String) txtcpp.getText());
            pst.setString(2, (String) txtddp.getText());
             pst.setString(3, (String) txtdp.getText());
            pst.setString(4, (String) txtnpp.getText());
              pst.setString(5, (String) txtfipp.getText());
                     pst.setString(6, (String) txtffpp.getText());
            pst.executeUpdate();
            
            
             txtnpp.setText("");
                 txtfipp.setText("");
                 txtffpp.setText("");
                txtdp .setText("");
                 txtddp.setText("");
                 txtcpp.setText("");
            ta.setText("Registro exitoso.");
        }catch (Exception e){
          
           ta.setText("error");   
        }


        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
try { Connection cn;
            cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from anuales where id = ?");
            pst.setString(1, txtcpp.getText().trim());
            ResultSet rs = pst.executeQuery();
            if (rs.next()) {
                
               txtnpp.setText(rs.getString("descuento"));
                 txtfipp.setText(rs.getString("descripcion"));
              txtffpp.setText(rs.getString("procentaje"));
                txtdp.setText(rs.getString ("fechai"));
                  txtddp.setText(rs.getString("fechaf"));
               txtcpp .setText(rs.getString ("id"));
           

            } else {
                JOptionPane.showMessageDialog(null, "Empleado no registrado.");
            }

        } catch (Exception e) {

        }
 


        // TODO add your handling code here:
    }//GEN-LAST:event_jButton4ActionPerformed

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modificarActionPerformed
 try {
            String ID = txtcpp.getText().trim();
            
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("update anules set codigo = ?, descuento = ?, descripcion = ? ,porcentaje = ?, fechai = ?, fechaf = ?where id = " + ID);
           
           
            pst.setString(1, (String) txtfipp.getText());
             pst.setString(2, (String) txtffpp.getText());
            pst.setString(3, (String) txtdp.getText());
              pst.setString(4, (String) txtddp.getText());
                     pst.setString(5, (String) txtcpp.getText());
     
            pst.executeUpdate();
            
            ta.setText("Modificaci√≥n exitosa.");
            
        } catch (Exception e) {
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_modificarActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
try {
             Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("delete anules where codigo = ?");
            
            pst.setString(1, txtcpp.getText().trim());
            pst.executeUpdate();
            
             txtnpp.setText("");
                 txtfipp.setText("");
                 txtffpp.setText("");
                txtdp .setText("");
                 txtddp.setText("");
                 txtcpp.setText("");
            
            ta.setText("Registro eliminado.");
            
        } catch (Exception e) {
        }


        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed

