    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
  try{
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
          
              PreparedStatement pst = cn.prepareStatement("insert into cd values(?,?,?,?)");
            
            pst.setString(1, "0");
            pst.setString(2, (String) txtn.getText());
            pst.setString(3, (String) txtg.getText());
            pst.setString(4, (String) txtp.getText());
        
            pst.executeUpdate();
            
            txtn.setText("");
                txtg.setText("");
                 txtp.setText("");
                txtpp.setText("");
           
            ta.setText("Registro exitoso.");
        }catch (Exception e){
          
           ta.setText("error");   
        }


        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
 try{
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from cd where id = ?");
            pst.setString(1, txtpp.getText().trim());
            
            ResultSet rs = pst.executeQuery();
            
            if(rs.next()){
                txtn.setText(rs.getString("nombre"));
                txtg.setText(rs.getString("genero"));
                 txtp.setText(rs.getString("precio"));
                txtpp.setText(rs.getString("Grupo"));
            } else {
              
            }
            
        }catch (Exception e){
            
        }


        // TODO add your handling code here:
    }//GEN-LAST:event_jButton4ActionPerformed

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modificarActionPerformed
 try {
            String ID = txtpp.getText().trim();
            
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("update cd set nombre = ?, genero = ?, precio = ? where id = " + ID);
           
             
            pst.setString(1, (String)   txtn.getText());
            pst.setString(2, (String)   txtg.getText());
             pst.setString(3, (String)  txtp.getText()); 
         
            pst.executeUpdate();
            
            ta.setText("Modificaci√≥n exitosa.");
            
        } catch (Exception e) {
        }



        // TODO add your handling code here:
    }//GEN-LAST:event_modificarActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
try {
             Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("delete cd where id = ?");
            
            pst.setString(1, txtpp.getText().trim());
            pst.executeUpdate();
          
               txtn.setText("");
                txtg.setText("");
                 txtp.setText("");
                txtpp.setText("");
           
            ta.setText("Registro eliminado.");
            
        } catch (Exception e) {
               ta.setText("error.");
        }



        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed

