    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");

            PreparedStatement pst = cn.prepareStatement("insert into diarios values(?,?,?,?,?,?)");

          
            pst.setString(1, (String) txtnpp1.getText());
            pst.setString(2, (String) txtfipp1.getText());
            pst.setString(3, txtfipp.getText());
            pst.setString(4, (String) txtcpp1.getText());
            pst.setString(5, (String) txtdp1.getText());
             pst.setString(6, (String) txtppp1.getText());
            pst.executeUpdate();

            txtnpp1.setText("");
                 txtfipp.setText("");
              txtfipp1.setText("");
                txtfipp.setText("");
                  txtcpp1.setText("");
               txtppp1 .setText("");

            ta.setText("Registro exitoso.");
        }catch (Exception e){

            ta.setText("error");
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

