    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
try { Connection cn;
            cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from diarios where id = ?");
            pst.setString(1, txtcpp1.getText().trim());
            ResultSet rs = pst.executeQuery();
            if (rs.next()) {
                
               txtnpp1.setText(rs.getString("descuento"));
                 txtfipp.setText(rs.getString("descripcion"));
              txtfipp1.setText(rs.getString("procentaje"));
                txtfipp.setText(rs.getString ("fechai"));
                  txtcpp1.setText(rs.getString("fechaf"));
               txtppp1 .setText(rs.getString ("codigo"));
           

            } else {
                JOptionPane.showMessageDialog(null, "Empleado no registrado.");
            }

        } catch (Exception e) {

        }
 

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton4ActionPerformed

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modificarActionPerformed
 try {
            String ID = txtppp1.getText().trim();
            
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("update diarios set descuento = ?,descripcion = ?,nombre = ?,fechai = ?,fechaf = ?where id = " + ID);
          
            pst.setString(1, (String) txtfipp1.getText());
            pst.setString(2, (String) txtfipp.getText());
            pst.setString(3, (String) txtcpp1.getText());
            pst.setString(4, (String) txtdp1.getText());
             pst.setString(5, (String) txtppp1.getText());
         
            pst.executeUpdate();
            
            ta.setText("Modificaci√≥n exitosa.");
            
        } catch (Exception e) {
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_modificarActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
try {
             Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/fm", "root", "");
            PreparedStatement pst = cn.prepareStatement("delete diarios where id = ?");
            
            pst.setString(1, txtppp1.getText().trim());
            pst.executeUpdate();
            
                txtnpp1.setText("");
                 txtfipp.setText("");
              txtfipp1.setText("");
                txtfipp.setText("");
                  txtcpp1.setText("");
               txtppp1 .setText("");
           
            ta.setText("Registro eliminado.");
            
        } catch (Exception e) {
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed

